import React from 'react';
import { Menu, Switch } from 'antd';
import { setNightMode } from './redux/actions';
import { useDispatch } from 'react-redux';

const Sidebar = () => {
  const dispatch = useDispatch();

  const handleNightModeToggle = (checked) => {
    dispatch(setNightMode(checked));
  };

  return (
    <div>
      {/* Your sidebar content here */}
      <Menu mode="inline">
        <Menu.Item key="1">Region 1</Menu.Item>
        <Menu.Item key="2">Region 2</Menu.Item>
        <Menu.Item key="3">Region 3</Menu.Item>
      </Menu>
      <div>
        <span>Night Mode:</span>
        <Switch onChange={handleNightModeToggle} />
      </div>
    </div>
  );
};

export default Sidebar;
