// src/components/Sidebar.js

import React from 'react';
import { Layout, Menu, Switch } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { setNightMode } from '../Sidebar/index.js';



const Sidebar = () => {
    const setNightMode = () => {
        // Your implementation for the setNightMode function
        return { type: 'SET_NIGHT_MODE', payload: true };
      };


    const dispatch = useDispatch();
    const isNightMode = useSelector((state) => state.isNightMode);
  
    const handleNightModeChange = (value) => {
      dispatch(setNightMode(value));
    };

    return (
        <Layout.Sider width={200} collapsible>
          <Menu theme="dark" mode="inline">
            <Menu.Item key="nightMode" icon={<Switch checked={isNightMode} onChange={handleNightModeChange} />}>
              Night Mode
            </Menu.Item>
          </Menu>
        </Layout.Sider>
      );
};

export { setNightMode };


export default Sidebar;
